mapper.update:
UPDATE monthly_card
validity_period = #{validityPeriod}, renew_time = #{renewTime}, status = #{status}, package_type = #{packageType}, amount = #{amount}, payment_method = #{paymentMethod},
WHERE card_id = #{cardId}

mapper.select:
SELECT mc.*, v.license_plate as vehicle_plate, u.name as owner_name
FROM monthly_card mc
LEFT JOIN vehicle v ON mc.vehicle_id = v.vehicle_id
LEFT JOIN sys_user u ON v.owner_id = u.user_id

    AND mc.vehicle_id = #{vehicleId}
    AND mc.status = #{status}

    AND v.owner_id = #{ownerId}

    AND v.user_id = #{userId}

ORDER BY mc.create_time DESC
