mapper.update:
UPDATE sys_user
name = #{name}, id_card = #{idCard}, email = #{email}, avatar = #{avatar}, last_login_time = #{lastLoginTime}, password = #{password},
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM sys_user
AND account LIKE CONCAT('%', #{account}, '%') AND phone LIKE CONCAT('%', #{phone}, '%') AND name LIKE CONCAT('%', #{name}, '%') AND is_owner_verified = #{isOwnerVerified}
ORDER BY create_time DESC
