mapper.select:
SELECT vehicle_id, license_plate, vehicle_type, owner_id, status FROM vehicle


        AND owner_id = #{ownerId}


        AND status = #{status}


ORDER BY vehicle_id ASC

mapper.update:
UPDATE vehicle
license_plate = #{licensePlate}, vehicle_type = #{vehicleType}, owner_id = #{ownerId}, status = #{status},
WHERE vehicle_id = #{vehicleId}
